interface ShopifyNode {
  id: string
  handle: string
  collections: {
    handle: string
  }[]
  descriptionHtml: string
  title: string
  tags: string[]
  status: string
  totalinventory?: number
  createdAt: string
  contextualPricing: {
    maxVariantPricing: {
      price: {
        amount: string
      },
      compareAtPrice: {
        amount: string
      } | null
    }
  }
  images: {
    shopifyId: string
    gatsbyImageData: any
  }[]
  featuredImage: {
    id: string
    gatsbyImageData: any
  }
}

const getRandomProduct = () => {
  const products = allShopifyProduct.edges
  const randomIndex = Math.floor(Math.random() * products.length)
  return products[randomIndex].node
}

const getRandomCategory = () => {
  const categories = allShopifyCollection.edges
  const randomIndex = Math.floor(Math.random() * categories.length)
  return categories[randomIndex].node
}

export const getProductNodes = (total: number) => {
  // return random nodes for testing
  const products: ShopifyNode[] = []

  for (let i = 0; i < total; i++) {
    // get random product from all products
    const randomProduct: ShopifyNode = getRandomProduct()

    products.push(randomProduct)
  }

  return products
}

export const getRandomCategoryNodes = (total: number) => {
  // return random nodes for testing

  const categories = []

  for (let i = 0; i < total; i++) {
    // get random product from all products
    const randomCategory = getRandomCategory()

    const category = {
      ...randomCategory,
      totalProducts: randomCategory.products.length
    }

    categories.push(category)
  }

  return categories
}
